import express from 'express';
import CartsController from '../controllers/cartsController.js';

const router = express.Router();
const cartsController = new CartsController();

router.post('/', cartsController.createCart.bind(cartsController));
router.get('/:cartId/products', cartsController.getCartProducts.bind(cartsController));
router.post('/:cartId/products', cartsController.addProductToCart.bind(cartsController));

export default router;