import express from 'express';
import ProductsController from '../controllers/productsController.js';

const router = express.Router();
const productsController = new ProductsController();

router.get('/', productsController.listProducts.bind(productsController));
router.get('/:id', productsController.getProduct.bind(productsController));
router.post('/', productsController.addProduct.bind(productsController));
router.put('/:id', productsController.updateProduct.bind(productsController));
router.delete('/:id', productsController.deleteProduct.bind(productsController));

export default router;